async def entrypoint(ctx: JobContext):
    try:
        logger.info(f"Conectando rÃ¡pidamente a la sala {ctx.room.name}")
        # Aumentar timeout para permitir carga completa de modelos
        await asyncio.wait_for(ctx.connect(), timeout=30.0)

        logger.info("Inicializando sesiÃ³n del agente...")

        # 1) Crear modelo LLM con configuraciÃ³n optimizada
        model = openai.realtime.RealtimeModel(
            voice="ash",
            model="gpt-4o-realtime-preview",
            temperature=0.4,  # Reducir temperatura para mÃ¡s precisiÃ³n
        )

        # 2) Pre-cargar VAD para acelerar inicializaciÃ³n
        logger.info("Cargando VAD...")
        vad = silero.VAD.load()
        
        # 3) Crear sesiÃ³n con componentes pre-cargados
        session = AgentSession(
            llm=model,
            vad=vad,
        )

        # 4) Crear e iniciar agente
        logger.info("Iniciando agente...")
        agent = GovLabAssistant()
        await session.start(
            room=ctx.room,
            agent=agent,
        )

        # 5) Generar saludo inicial exacto
        await session.generate_reply(
            instructions="Di exactamente este texto sin cambios ni adiciones: 'Â¡Hola! Soy Gober, el asistente virtual de Santander Territorio inteligente. Puedes preguntarme sobre los objetivos estratÃ©gicos y avances del departamento. Â¿En quÃ© puedo ayudarte hoy?'"
        )

        logger.info("Agente conectado y listo para usar")

    except Exception as e:
        logger.error(f"Error in entrypoint: {e}", exc_info=True)
        raise

